	REP.b #$20
	LDA.w #SMW_Map16Data_Backgrounds
	STA.b !RAM_SMW_Misc_ScratchRAM00
	LDX.w #$0000
CODE_0581A5:
	LDA.b !RAM_SMW_Misc_ScratchRAM00
